function UpdateTime() {
    var CurrentTime = new Date();
    var InputTime = $('.time');
    h = CurrentTime.getHours();
    if (h < 10) h = "0" + h;
    m = CurrentTime.getMinutes();
    if (m < 10) m = "0" + m;
    s = CurrentTime.getSeconds();
    if (s < 10) s = "0" + s;
    outString = h + ":" + m + ":" + s;
    InputTime.text(outString);

    setTimeout("UpdateTime()", 1000);
}

